def decoded_packets():
    return [
        parse_position_packet(packet_data_1),
        parse_position_packet(packet_data_2),
        parse_position_packet(packet_data_3),
        parse_position_packet(packet_data_4),
    ]


def test_parse_position_packet_timestamp(decoded_packets: list[PositionData]):
    assert decoded_packets[0].timestamp == 291920277
    assert decoded_packets[1].timestamp == 291973479


def test_parse_position_packet_pulse_pre_sec(decoded_packets: list[PositionData]):
    assert decoded_packets[0].pulse_per_second == 0
    assert decoded_packets[1].pulse_per_second == 0


def test_parse_NMEA_data_happy_path():
    NEMA_data = parse_NMEA_data(packet_data_3)
    assert NEMA_data is not None
    assert len(NEMA_data.sentence) != 0
    assert NEMA_data.time == "212446"
    assert NEMA_data.status == "A"
    assert pytest.approx(NEMA_data.latitude) == 37.137861666666666
    assert pytest.approx(NEMA_data.longitude) == -121.649838333333
    assert NEMA_data.speed == 007.5
    assert NEMA_data.track_angle == 006.5
    assert NEMA_data.date == "111016"
    assert NEMA_data.mag_var == 013.6
    assert NEMA_data.mag_var_dir == "E"
    assert NEMA_data.fix_mode == "D"
    assert NEMA_data.checksum == "0D"


def test_parse_NMEA_data():
    NEMA_data = parse_NMEA_data(packet_data_1)
    assert NEMA_data is None
